insert into eg_roleaction (roleid,actionid) select (select id from eg_role where name='Water Sewerage Administrator'),(select id from eg_action where name='SearchWaterTaxActivateDeactivate') where not exists (select * from eg_roleaction where roleid=(select id from eg_role where name='Water Sewerage Administrator') and actionid=(select id from eg_action where name='SearchWaterTaxActivateDeactivate'));
