update egwtr_connection set locality=null;
update egswtax_connection set locality=null;
delete from eg_boundary where boundarytype=(select id from eg_boundary_type where hierarchytype = (select id from eg_hierarchy_type where name = 'REVENUE') and name='Locality');

update egwtr_connection conn set locality=(select loc.id from eg_boundary loc, egwtr_stg_connection stg where loc.boundarytype=(select id from eg_boundary_type where name='Locality') and stg.locality=loc.code and stg.oldconsumernumber=conn.oldconsumernumber) where locality is null;

update egwtr_connection set block=(select parent from eg_boundary where id=locality);

update egwtr_connection set zone=(select parent from eg_boundary where id=block);

update egswtax_connection conn set locality=(select loc.id from eg_boundary loc, egswtax_stg_connection stg where loc.boundarytype=(select id from eg_boundary_type where name='Locality') and stg.locality=loc.code and stg.oldconsumernumber=conn.oldconsumernumber) where locality is null;

update egswtax_connection set block=(select parent from eg_boundary where id=locality);

update egswtax_connection set zone=(select parent from eg_boundary where id=block);
